import React, { Component } from 'react';
import './Form.css';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'

class Form extends Component{

  constructor() {  
    super();  
    this.HandleClick = this.HandleClick.bind(this);  
  }  

// handlename = (event) => {
  
//   this.setState({ fullname: event.target.value })
// }

// handleemail = (event) => {
  
//   this.setState({ email: event.target.value })
// }

// handlenumber = (event) => {
  
//   this.setState({ number: event.target.value })
// }

// handlemessage = (event) => {
  
//   this.setState({ message: event.target.value })
// }

handlechangeall = (event) =>{
 this.setState ( { [event.target.name] :event.target.value  } )
}

 
HandleClick() {  
  Swal.fire({  
    title: 'Success',  
    type: 'success',  
    text: 'Your work has been saved.',  
  });  
}  
render(){
 return(
   <div className="contact-area overflow-hidden default-padding bg-gray" style={{paddingTop:"35px"}}>
  <div className="form-container">
  <div className="form-row">
  <div className="col-lg-6 contact-form-box">

   <form onSubmit = {this.handlesubmit} >
    <label> FullName </label><br/>
    <input  type="text" name="fullname"  value={this.state.fullname}  placeholder="Enter your FullName"
      onChange={this.handlechangeall} /> <br/>

    <label> Email </label><br/>
    <input  type="email" name="email" value= {this.state.email} placeholder="Enter your email"
      onChange={this.handlechangeall} /> <br/>

    <label> Mobile </label><br/>
    <input  type="number" name="phone" value= {this.state.phone} placeholder="Enter your Mobile Number"
            onChange={this.handlechangeall} /> <br/>

    <label> Message </label><br/>
    <textarea  value= {this.state.message}  name="message" placeholder="Enter your Message"
            onChange={this.handlechangeall} />   <br/>

    <input type="submit" value="Send" />
   </form>
   </div>
   </div>
  </div>
  </div>
 )
};

};

export default Form;
